CREATE TABLE IF NOT EXISTS books (
    id INT PRIMARY KEY AUTO_INCREMENT,
    book_name varchar(255),
    publish_year smallint,
    image_path varchar(255),
    book_description text,
    author_1 varchar(255) NULL,
    author_2 varchar(255) NULL,
    author_3 varchar(255) NULL,
    is_deleted BOOLEAN DEFAULT FALSE,
    visits INT DEFAULT 0,
    wants INT DEFAULT 0
);

INSERT INTO
	books(book_name, publish_year, image_path, book_description, author_1, author_2, author_3)
VALUES
	("1984", 1949, "1984.jpg", "Незважаючи на художню вигадку, книга настільки реалістична, що перевертає свідомість, причому так, що хочеться перевернути її назад. Сам письменник стверджував, що «найкращі книги говорять тобі те, що ти вже сам знаєш». І роман-утопія «1984» описує речі, про які ми самі здогадувалися, але у існування яких боялися повірити.", "Джордж Орвелл", NULL, NULL),
	("Кобзар", 2015, "кобзар.jpg", "До збірки, крім основних, увійшли твори, що їх було вилучено у різні часи царською, а згодом радянською цензурою. Наведено також біографічні й бібліографічні відомості та добірку афоризмів поета.", "Тарас Шевченко", NULL, NULL),
	("Конотопська відьма", 2017, "конотопська_відьма.jpg", "До видання увійшли твори видатного українського письменника Г. Квітки-Основ'яненка, якого відомий літературознавець С. Єфремов назвав батьком української повісті. Добір поданих текстів обумовлений шкільною програмою з української літератури. Розраховано на школярів, вчителів, студентів-філологів, усіх, хто цікавиться творчістю Квітки-Основ'яненка.", "Григорій Квітка-Основ'яненко", NULL, NULL),
	("Павлiк Морозов", 2013, "павлік_морозов.jpg", "Лесь Подерв’янський (1952) - скандально відомий український письменник і художник (член спілки художників України), культова фігура київського андерґраунду 70-90-х років ХХ століття. Справжню популярність Лесю Подерв’янському принесли його епатажнi п’єси, що були спочатку записанi на аудiокасетах і СD-дисках, а тепер з його творами можна ознайомитися на сторінках книжок. Читаючи Подерв’янського, ви відкриєте для себе, що його герої, на жаль, не такі вже нереальні, як це може здатися на перший погляд, а тонка іронія та гумор автора дозволять вам ще і ще раз посміхнутися над трагікомізмом нашого життя.", "Лесь Подерв’янський", NULL, NULL),
	("The Secret Garde", 1911, "the_secret_garden.jpg", "arperCollins is proud to present its new range of best-loved, essential classics. Orphaned and sent to live with her uncle in his austere manor on the moors, Mary Lennox is a lonely and unhappy child. A meeting with Dickon, her servant's brother begins her adventure and it is through their friendship and her relationship with her troubled hypochondriac cousin Colin that she begins to learn about herself. Their lives all begin to change when a Robin shows Mary the door to a mysterious secret garden.", "Френсіс Бернетт", NULL, NULL),
	("Робинзон Крузо", 1719, "робинзон_крузо.jpg", "дна из самых известных в мире книг рассказывает об удивительных приключениях моряка, потерпевшего кораблекрушение и волею судьбы выброшенного на берег необитаемого острова. `Робинзон Крузо` Даниэля Дефо - бессмертный гимн человеческому мужеству и смекалке.", "Данiель Дефо", NULL, NULL),
	("Книга Гаррі Поттер і таємна кімната", 1998, "гаррі_потер.jpg", "Друга частина пригод Гаррі Поттера від Джоан Роулінг — книга «Гаррі Поттер і таємна кімната»! На вас чекають нові пригоди у світі чарівників, де старі секрети, літаючі автомобілі, грізна верба, дивний щоденник та небезпечні улюбленці Хегріда оживають на сторінках книги!", "Джоан Роулінг", NULL, NULL),
	("Книга Відьмак. Останнє бажання. Книга 1", 1993, "відьмак.jpg", "Біловолосий відьмак Ґеральт із Рівії, один з небагатьох представників колись численного цеху захисників людської раси від породжень нелюдського зла, мандрує невеликими королівствами, які можна охопити поглядом з вежі замку, та великими містами, отримуючи платню за те, чого навчений, - знищення віїв і з'ядарок, стриґ та віпперів. Але є у відьмака і власний кодекс, у якому вбивство - це лише крайня міра, а життя розумне, чим би воно не було, - це все-таки життя. Саме цим він наживає собі нових ворогів, але й знаходить друзів, які колись змінять його долю.", "Анджей Сапковський", NULL, NULL),
	("Код да Вінчі", 2003, "код_да_вінчі.jpg", "Ця історія про неймовірне розслідування і приголомшливі відкриття Роберта Ленґдона та Софі Неве, без перебільшення, перевернула світ. Ден Браун створив легенду, в яку повірили всі, — попри відчайдушний опір Ватикану. Скандали навкруг роману, здається, не вгамуються ніколи, чому сприяє недавня його екранізація. А секрет цього величезного успіху полягає в тому, що Денові Брауну вдалося, як нікому до нього, наочно довести, що так звана історія та політика — це тільки ширма, за якою приховані величезні таємниці", "Ден Браун", NULL, NULL),
	("Сто років самотності", 1967, "сто_років_самотності.png", "Ґабріель Ґарсія Маркес (1927-2014) – колумбійський журналіст, видавець і політичний діяч, один з найвидатніших письменників ХХ століття. Засновник літературного напряму «магічний реалізм». На початку 1960-х років Маркес переїхав з Колумбії до Мексики, і у 1981 році попросив політичного притулку. У 1982 році йому було присуджено Нобелівську премію з літератури: «за романи та оповідання, в яких фантазія та реальність, поєднуючись, відображають життя і конфлікти цього континенту».", "Ґабріель Ґарсія Маркес", NULL, NULL),
	("Зелена миля", 2018, "зелена_миля.jpg", "Пол Еджкомб — колишній наглядач федеральної в’язниці штату Луїзіана «Холодна гора», а нині — мешканець будинку для літніх людей. Більш ніж півстоліття тому він скоїв те, чого досі не може собі вибачити. І тягар минулого знову й знову повертає його до 1932 року.", "Стівен Кінг", NULL, NULL),
	("Маленький принц", 1943, "маленький_принц.jpg", "Видатний французький письменник Антуан де Сент-Екзюпері був відважним льотчиком. Одного разу на своєму маленькому поштовому літаку він навіть перелетів Атлантичний океан. Це був світовий рекорд. Якось письменник зазнав аварії над Лівійською пустелею. Доки він десять днів ремонтував свій літак, до нього навідувався маленький пустельний лис. Вони заприятелювали. Згодом після тієї пригоди Екзюпері написав свій найгеніальніший твір — казку «Маленький принц»...", "Антуан де Сент-Екзюпері", NULL, NULL),
	("Білий попіл", 2018, "білий_попіл.jpg", "Приватному детективу Тарасу Білому замовляють розслідувати вбивство панночки. Обвинувачують такого собі семінариста Хому Брута, й справа ніби геть зрозуміла... Втім, хутір, де сталося вбивство, приховує таємницю. І ця таємниця — страшніша за всі оповідки про панночку, яка встала з гробу.", "Ілларіон Павлюк", NULL, NULL),
	("Хід королеви", 1983, "хід_королеви.jpg", "1950-ті роки. Бет Гармон — звичайна сирота із незвичайним хистом до шахів. Гра — її єдина пристрасть. Доки Бет у грі, доти контролює себе та... згубну жагу до транквілізаторів і алкоголю. Дівчина відчайдушно бажає стати феноменальною шахісткою, але водночас усе дужче хоче втекти подалі від цієї реальності.", "Вальтер Тевіс", NULL, NULL),
	("Тигролови", 1944, "тигролови.jpg", "У романі відомого українського письменника Івана Багряного «Тигролови» розповідається про жахливі сторінки історії українського народу — сталінські репресії 30-х років ХХ ст. Рекомендовано програмою загальноосвітньої школи з української літератури.", "Іван Багряний", NULL, NULL),
	("Список Шиндлера", 2020, "список_шиндлера.jpg", "В основу роману лягли спогади в`язня краківського гетто, приреченого на смерть і врятованого Шиндлером. Успішний бізнесмен Оскар Шиндлер відкриває в Кракові фабрику з виготовлення посуду. Для нього євреї з гетто і табору Плашув — просто дешева робоча сила. Однак зовсім скоро він відчуває безмежну повагу та співчуття до своїх робітників. Коли людей з Плашува починають перевозити до Аушвіцу, Оскар за допомогою свого друга, Іцхака Штерна, складає список тих, кого він нібито бере на фабрику. До нього увійшли понад тисячу людей, приречених на смерть. Аби врятувати їх, Шиндлер не шкодував ані грошей, ані сил, ані часу.", "Томас Кинилли", NULL, NULL),
	("451° за Фаренгейтом", 1953, "фаренгейт.jpg", "Що трапилося з нашим світом? Чому пожежники спалюють будинки, а не гасять пожежі? Чому люди не читають книги, не ходять пішки і майже не розмовляють одне з одним? Як виглядатиме наша Земля та якими будуть люди, якщо заборонити їм читати книги? Зі сторінок одного з найкращих творів Рея Бредбері ви дізнаєтеся, як люди навчаться зберігати книги для наступних поколінь, хто повстане проти пануючої системи і як зустріч із «чудною» дівчиною Кларіс змінить життя пожежника Ґая Монтеґа…", "Рей Бредбері", NULL, NULL),
	("Морт", 1987, "морт.jpg", "Роман «Морт» відкриває цикл серії «Дискосвіт», у якому головним персонажем є Смерть. Та це не означає, що книжка моторошна, адже у світі Террі Пратчетта Смерть рибалить, філософує, любить кошенят і смачні страви, мріє про відпустку і врешті просто виконує свою роботу: доправляє душі в інший світ. Думки про відпочинок спонукають Смерть обрати собі в підмайстри сільського хлопця Морта.", "Террі Пратчетт", NULL, NULL),
	("Три товариші", 1936, "три_товариші.jpg", "Один з найкращих світових романів про справжню дружбу і велике кохання... Війна обпекла душі людей, і рани ще не загоїлися. Але все можна подолати, коли поруч є два товариші і кохана.", "Еріх Марія Ремарк", NULL, NULL),
	("Кульбабове вино", 2015, "кульбабове_вино.jpg", "Літо у неіснуючому містечку Ґрін Таун 1928 року… Веселі пустощі чотирьох безтурботних хлопчаків… І кульбабове вино, яке готує дідусь… Воно – як чарівна капсула часу, що вбирає у себе досвід минулих літ, воно – як ностальгія за дитинством, у яке зможе хоча б на мить повернутися кожен, хто візьме до рук фантастичну повість Рея Бредбері «Кульбабове вино». Ця книга – острівець, на якому зупиняється час. Її сторінки дихають далекими дитинно-безмежними спогадами – теплими, п’янкими, вічними, проте яких нам не спіймати ніколи.", "Рей Бредбері", NULL, NULL),
	("Божественна комедія", 2018, "божествення_комедія.jpg", "Плід усього життя «Суворого Данта» (так назвав геніального італійця Пушкін), творіння, яке в епоху середньовіччя стало передвісником Відродження, праця, що стоїть у ряду найбільших досягнень людської думки, — так говорили, говорять і говоритимуть про твір, який сам Данте Аліг'єрі назвав просто «комедією», а його нащадки нарекли «Божественною». У своєму найграндіознішому витворі в символічно-алегоричній формі поет зобразив драматичну долю людської душі: її загибель у пеклі, переродження в чистилищі, тріумф у раю.", "Данте Аліг'єрі", NULL, NULL),
	("Сяйво", 2022, "сяйво.jpg", "Коли Торранси найнялися доглядати взимку за розкішним готелем, вони й гадки не мали, який невимовний жах чекає на них... Одного разу там скоїлася страшна трагедія: колишній доглядач зарубав сокирою власну родину. Саме тут п’ятирічний Денні дізнався, що він може бачити справжніх мешканців будинку. І це — привиди. Хлопчику, наділеному даром передбачення, відкривається страшна суть речей. Він уже знає, звідки його родині загрожує смерть...", "Стівен Кінг", NULL, NULL),
	("Говард Філіпс Лавкрафт. Повне зібрання прозових творів (комплект із 3 книг)", 2018, "лавкрафт.png", "Говард Філіпс Лавкрафт (1890-1937) - відомий американський письменник, який, написавши величезну кількість оповідань, багато повістей і три романи (не кажучи вже про архів його листування, що вважається найбільшим у світі), на жаль, за життя був майже невідомим і помер у самотності, так і не дочекавшись майбутньої всесвітньої слави свого вагомогу літературного доробку. Проте вже у другій половині ХХ сторіччя творчість Лавкрафта заслужено отримує світову славу і здобуває захоплення мільйонів її поціновувачів.", "Говард Лавкрафт", NULL, NULL),
	("Ніч у Лісабоні", 2020, "ніч_у_лісабоні.jpg", "«Життя коротке… Але що ж робить його таким? Усвідомлення того, що воно коротке…» Врятуватися, втекти з Європи якомога далі від нацистів! Їхня остання надія — пароплав до Америки. Але знайти квитки на нього майже неможливо… Дивний незнайомець готовий віддати власні — в обмін на те, що його вислухають. Попереду ніч, сповнена вина й довгих розмов про життя, націю, втрату та справжнє кохання під час війни…", "Еріх Марія Ремарк", NULL, NULL),
	("Крадійка книжок", 2020, "крадійка_книжок.jpg", "Вражаюча історія, яку розповідає... Смерть! До влади в Німеччині прийшли фашисти. Вперше Смерть побачив маленьку Лізель, коли прийшов забрати душу її братика. Він став свідком того, як дівчинка вкрала першу книжку, і зацікавився її долею. Він приходив до її будинку, де, ризикуючи життям, родина переховувала єврея. Він слухав, як Лізель читала книжки під час бомбардувань. Смерть завжди був поруч. Під час останньої зустрічі він розкриє крадійці книжок свою найбільшу таємницю...", "Маркус Зузак", NULL, NULL);

    CREATE TABLE IF NOT EXISTS sessions_v1(id varchar(255) PRIMARY KEY);